// import CompC from './CompC';
import {Firstname,Lasttname} from '../../jsx/App';
const CompB=()=>{
    const fname=useContext(Firstname);
    const lname=useContext(Lasttname);
    // return <CompC/>;
    return(
        <>
            <h1>My Name is {fname} {lname}</h1>
        </>
    );

};
export default CompB;
