                    email:prevalue.email,
                    mobile:prevalue.mobile,
                };
            }
           else if(name==="lname")
            {
                return{
                    fname:prevalue.fname,
                    lname:value,
                    email:prevalue.email,
                    mobile:prevalue.mobile,
                };
            }
            else if(name==="email")
            {
                return{
                    fname:prevalue.fname,
                    lname:prevalue.fname,
                    email:value,
                    mobile:prevalue.mobile,
                };
            }
            else if(name==="mobile")
            {
                return{
                    fname:prevalue.fname,
                    lname:prevalue.fname,
                    email:prevalue.email,
                    mobile:value,
                };
            }*/
        });
    };
