import React, { useState } from 'react';
// const state = useState();

const Hook=()=>{
//syntax
    //const [var,func]=useState(initial_data);
    const [count,setCount]=useState(0);
    const IncNum=()=>{
      setCount(count+1);  
    };
    return(
        <>
            <h1>{count}</h1>
            <button onClick={IncNum}>Click Me</button>
        </>
    );
};

export default Hook;
