import React, { useState } from 'react';

const Form=()=>{
    // const[name,setName]=useState("");
    const[fullname,setFullname]=useState({
        fname:'',
        pass:'',
    });
    // const[pass,setPass]=useState("");
    // const[fullname,setFullName]=useState();
    const nameEvent=(event)=>{
        // console.log(event.target.value);
        // setName(event.target.value);
        const value=event.target.value;
        const name=event.target.name;
        setFullname((prevalue)=>{
            if(name==="firName")
            {
                return{
                    fname:value,
                    pass:prevalue.pass,
                };
            }
           else if(name==="Pass")
            {
                return{
                    fname:prevalue.fname,
                    pass:value,
                };
            }
        });
    };
    // const passEvent=(event)=>{
    //     // console.log(event.target.value);
    //     setPass(event.target.value);
    // };
    const onSubmit=(event)=>{
/*The preventDefault() method cancels the event if it is cancelable, meaning that the default action that belongs to the event will not occur.
For example, this can be useful when:
Clicking on a "Submit" button, prevent it from submitting a form
Clicking on a link, prevent the link from following the URL */
        event.preventDefault();
        // let full=name + " " + pass;
        // setFullName(full);
        alert('form submitted');
    };
    return(
        <>
            <form onSubmit={onSubmit} style={{width:'100%'}}>
                <div>
                    {/* <h1>Hello {fullname}</h1> */}
                    <h1>Hello {fullname.name} {fullname.pass}</h1>
                    <input type="text" placeholder="Enter your name" name="firName" onChange={nameEvent} value={fullname.fname}/>
                    <input type="text" placeholder="Enter your Password" name="Pass" onChange={nameEvent} value={fullname.pass}/>
                    <button type="submit"> Click üòè</button>
                </div>
            </form>
        </>
    );
};

export default Form;
