import React, { useState } from 'react';

const Event=()=>{
    const purple="#8e44ad";
    const [bg,setBg]=useState(purple);
    const[name,setName]=useState('Click Me');
    const bgChange=()=>{
       let newBg='#34495e';
       let newName='Ouch !! üê±';
        setBg(newBg);
        setName(newName);
    };
    return(
        <>
            <div style={{backgroundColor:bg}}>
                <button onClick={bgChange} >{name}</button>
            </div>
        </>
    );
};

export default Event;
